const e=document.getElementById("backToTop");window.addEventListener("scroll",()=>{window.pageYOffset>300?(e.classList.remove("opacity-0","invisible"),e.classList.add("opacity-100","visible")):(e.classList.add("opacity-0","invisible"),e.classList.remove("opacity-100","visible"))});e.addEventListener("click",()=>{window.scrollTo({top:0,behavior:"smooth"})});const t=document.getElementById("mobileMenuToggle"),o=document.getElementById("mobileMenu");t?.addEventListener("click",()=>{o?.classList.toggle("-translate-x-full")});const l=document.getElementById("darkModeToggle");l?.addEventListener("click",()=>{document.documentElement.classList.toggle("dark"),localStorage.setItem("darkMode",document.documentElement.classList.contains("dark")?"true":"false")});(localStorage.getItem("darkMode")==="true"||!localStorage.getItem("darkMode")&&window.matchMedia("(prefers-color-scheme: dark)").matches)&&document.documentElement.classList.add("dark");
